import React from 'react'
import { useState, useEffect } from 'react'

export default function Home() {
    const [shows, setShows] = useState([])
    const [input, setInput] = useState()
    const [display, setDisplay] = useState(false)
    const [movieName, setMovieName] = useState(null)
    const [movieData, setMovieData] = useState({})

    useEffect(() => {
        if (movieName) {
            fetch(`http://www.omdbapi.com/?t=${movieName}&apikey=3244219b`)
                .then(response => response.json())
                .then(data => {
                    setMovieData(data);
                });
        }
    }, [movieName]);

    const fetchMovies = (query) => {
        fetch(`http://www.omdbapi.com/?s=${query}&apikey=3244219b`)
            .then(response => response.json())
            .then(data => {
                if (data.Search) {
                    const lastFiveMovies = data.Search.slice(-5);
                    setShows(lastFiveMovies);
                }
            });
    };

    const handleInput = (e) => {
        const query = e.target.value;
        setInput(query);
        fetchMovies(query);
        setDisplay(false);
    }


  return (
    <div>
        <div className="movie">
        <nav id="navbar-example2" class="navbar navbar-light bg-light px-3">
  <a class="navbar-brand" href="#">Navbar</a>
  <ul class="nav nav-pills">
    <li class="nav-item">
      <a class="nav-link" href="#scrollspyHeading1">First</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" href="#scrollspyHeading2">Second</a>
    </li>
    <li class="nav-item dropdown">
      <a class="nav-link dropdown-toggle" data-bs-toggle="dropdown" href="#" role="button" aria-expanded="false">Dropdown</a>
      <ul class="dropdown-menu">
        <li><a class="dropdown-item" href="#scrollspyHeading3">Third</a></li>
        <li><a class="dropdown-item" href="#scrollspyHeading4">Fourth</a></li>
        <li><hr class="dropdown-divider"/></li>
        <li><a class="dropdown-item" href="#scrollspyHeading5">Fifth</a></li>
      </ul>
    </li>
  </ul>
</nav>
<div data-bs-spy="scroll" data-bs-target="#navbar-example2" data-bs-offset="0" class="scrollspy-example" tabindex="0">
  <h4 id="scrollspyHeading1">First heading</h4>
  <p>...</p>
  <h4 id="scrollspyHeading2">Second heading</h4>
  <p>...</p>
  <h4 id="scrollspyHeading3">Third heading</h4>
  <p>...</p>
  <h4 id="scrollspyHeading4">Fourth heading</h4>
  <p>...</p>
  <h4 id="scrollspyHeading5">Fifth heading</h4>
  <p>...</p>
</div>

            <input className='searchField' type="text" onChange={handleInput} name="findMovie" value={movieName} placeholder='Find a show' id="" />
            {input && shows.length > 0 && display === false?
                <div>
                    {shows.map((show, index) => (
                        <div key={index} className='scroll' onClick={() => {/*console.log(show.title)*/; setMovieName(show.Title); setDisplay(true)}}>
                            <div className="text">
                                <h4>{show.Title}</h4>
                                <div className="small">
                                    <p>{show.Year}</p>
                                    <p>o</p>
                                    <p>{show.Runtime}</p>
                                    <p>o</p>
                                    <p>{show.Type? show.Type[0].toUpperCase() + show.Type.slice(1, show.Type.length) : null}</p>
                                    <p style={{display: "none"}}>{show.Released}{show.imdbRating}</p>
                                </div>
                            </div>
                            <div className="image">
                                <img src={show.Poster ? show.Poster : null} width="60px" height="80px" alt="" />
                            </div>
                        </div>
                        ))
                    }
                </div> 
                : 
                <div className='movie-container'>
                    <h3>{movieData.Title}</h3>
                    <p>{movieData.Type? movieData.Type[0].toUpperCase() + movieData.Type.slice(1, movieData.Type.length) : null}</p>
                    <p>{movieData.Released}</p>
                    <p>{movieData.Runtime}</p>
                    <p>IMDB RATING: {movieData.imdbRating}</p>
                    <p>Your Rating</p>
                    <img src={movieData.Poster} alt="" />
                    <p>{movieData.Genre}</p>
                    <p>Director: {movieData.Director}</p>
                    <p>Writes: {movieData.Writer}</p>
                    <p>Actors: {movieData.Actors}</p>
                    <p>{movieData.Plot}</p>
                    <p>Country: {movieData.Country}</p>
                </div>}
            
        </div>
    </div>
  )
}
