import React, {useState, useEffect} from 'react'
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function Watchlist() {
  const { userId } = useParams();
  const [loading, setLoading] = useState(true);
  const [watchlist, setwatchlist] = useState([])
  const [error, setError] = useState(null)

  useEffect(() => {
    axios.get(`http://localhost:3000/movies/${userId}/watchlist`)
    .then(response => {
      const data = response.data
      setwatchlist(data)
      setLoading(false)
    })
    .catch((error) => {
      console.error('Error fetching user data:', error);
      setError(error)
      setLoading(false)
  })}, [userId])

  const deleteWatchlist = (index) => {
    const movieToDelete = watchlist[index]
    console.log(movieToDelete.id)
    axios.delete(`http://localhost:3000/movies/${userId}/watchlist/${movieToDelete.id}`)
    .then((response) => {
      console.log(response)
      const updatedwatchlist = watchlist.filter((movie, i) => i !== index);
      setwatchlist(updatedwatchlist);
    })
    .catch((error) => {
      console.log(error)
    })
  }
  return (
    <div>
      <h1>My Watchlist:</h1>
    </div>
  )
}
