import React from 'react'
import { useState } from 'react'

export default function Home() {
    const [shows, setShows] = useState([])
    const [input, setInput] = useState()
    const [display, setDisplay] = useState(false)
    const [movieName, setMovieName] = useState(null)
    const [movieData, setMovieData] = useState(null);

    const fetchMovies = (query) => {
        fetch(`http://www.omdbapi.com/?s=${query}&apikey=3244219b`)
            .then(response => response.json())
            .then(data => {
                if (data.Search) {
                    const lastFiveMovies = data.Search.slice(-5);
                    setShows(lastFiveMovies);
                }
            });
    };

    const handleInput = (e) => {
        const query = e.target.value;
        setInput(query);
        fetchMovies(query);
        setDisplay(false)
    }

    const fetchMovieDetails = (name) => {
        // First, search for the movie by name
        fetch(`http://www.omdbapi.com/?s=${name}&apikey=3244219b`)
            .then(response => response.json())
            .then(data => {
                if (data.Search && data.Search.length > 0) {
                    // Assuming the first result in the search is the desired movie
                    const imdbID = data.Search[0].imdbID;
                    
                    // Now fetch the details using the IMDb ID
                    fetch(`http://www.omdbapi.com/?i=${imdbID}&apikey=3244219b`)
                        .then(response => response.json())
                        .then(movieData => {
                            setMovieData(movieData);
                            setDisplay(true);
                        });
                }
            });
    };


  return (
    <div>
        <div className="movie">
            <input className='searchField' type="text" onChange={handleInput} name="findMovie" value={input} placeholder='Find a show' id="" />
            {input && shows.length > 0 && display === false?
                <div>
                    {shows.map((show, index) => (
                        <div key={index} className='scroll' onClick={() => setMovieName(show.Title)}>
                            <div className="text">
                                <h4>{show.Title}</h4>
                                <div className="small">
                                    <p>{show.Year}</p>
                                    <p>o</p>
                                    <p>{show.Runtime}</p>
                                    <p>o</p>
                                    <p>{show.Type? show.Type[0].toUpperCase() + show.Type.slice(1, show.Type.length) : null}</p>
                                    <p style={{display: "none"}}>{show.Released}{show.imdbRating}</p>
                                </div>
                            </div>
                            <div className="image">
                                <img src={show.Poster ? show.Poster : null} width="60px" height="80px" alt="" />
                            </div>
                        </div>
                        ))
                    }
                </div> 
                : 
                <div className='movie-container'>
                    <h3>{movieData.Title}</h3>
                    <p>{movieData.Type? movieData.Type[0].toUpperCase() + movieData.Type.slice(1, movieData.Type.length) : null}</p>
                    <p>{movieData.Released}</p>
                    <p>{movieData.Runtime}</p>
                    <p>IMDB RATING: {movieData.imdbRating}</p>
                    <p>Your Rating</p>
                    <img src={movieData.Poster} alt="" />
                    <p>{movieData.Genre}</p>
                    <p>Director: {movieData.Director}</p>
                    <p>Writes: {movieData.Writer}</p>
                    <p>Actors: {movieData.Actors}</p>
                    <p>{movieData.Plot}</p>
                    <p>Country: {movieData.Country}</p>
                </div>
            }
            
        </div>
    </div>
  )
}
