import './App.css';
import { BrowserRouter, Route, Routes, Link, useParams } from 'react-router-dom';
import { useState, useEffect } from 'react'
import Home from './pages/home'
import Top from './pages/top'
import Auth from './pages/auth'
import Watched from './pages/watched'
import Watchlist from './pages/watchlist'
import Movie from './pages/movie'
import { useAuth } from './functions/context'
import axios from 'axios'

function App() {
  const [visible, setVisible] = useState(false);
  const [theId, setTheId] = useState(null);
  const [search, setSearch] = useState("hey")
  const {authenticated, setAuthenticated} = useAuth()

  const isAuthenticated = localStorage.getItem("authenticated")
  const userId = localStorage.getItem("userId")

  useEffect(() => {
    const isAuthenticated = localStorage.getItem('authenticated');
    setAuthenticated(isAuthenticated === 'true');
  }, [setAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem("authenticated")
    setAuthenticated(false);
    localStorage.removeItem("userId")
    window.location.reload();
    console.log("logout: ", authenticated)
  }

  const handleChange = (e) => {
    setSearch(e.target.value)
    console.log(search)
  }

  const findUser = (e, theId) => {
    e.preventDefault()
    if (e.key === "Enter") {
      axios.get('http://localhost:3000/users/')
        .then(response => {
          for (let i = 0; i < response.data.length; i++) {
            if (response.data[i].username === theId) {
              setTheId(response.data[i].id)
            }
          console.log(response.data[0].username)
        }})
        .catch(error => {
          console.error(error);
        });
    }
  }

  return (
    <div className="App">
      <BrowserRouter>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <div className="navbar-brand"></div>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            {isAuthenticated &&
            <form class="form-inline my-2 my-lg-0">
              <input onChange={handleChange} onKeyDown={findUser} value={search} className="find-user" type="search" placeholder="Find a User's Top 100" aria-label="Search"/>
            </form>}
            <div className='d-flex'>
              <div className="collapse navbar-collapse" id="navbarNavDropdown">
                <ul className="navbar-nav">
                  <li className="nav-item">
                    <Link className='link-li' to={isAuthenticated ?`/${userId}`: '/'}>
                      <div>Home</div>
                    </Link>
                  </li>
                  {authenticated?
                  <li className="nav-item dropdown">
                    <div className="nav-link dropdown-toggle" id="navbarDropdownMenuLink" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                      My Movies
                    </div>
                    <ul className="dropdown-menu custom-dropdown" aria-labelledby="navbarDropdownMenuLink">
                      <li>
                        <Link className='link-li' to={`/${userId}/top`}>
                          <div className='drop-item'>My Top 100 Movies</div>
                        </Link>
                      </li>
                      <li>
                        <Link className='link-li' to={`/${userId}/watchlist`}>
                          <div className='drop-item'>My Watchlist</div>
                        </Link>
                      </li>
                        <li>
                          <Link className='link-li' to={`/${userId}/watched`}>
                            <div className='drop-item'>Watched</div>
                          </Link>
                      </li>
                    </ul>
                  </li>
                  : null }
                  {isAuthenticated?
                  <li className="nav-item">
                      <div className='link-li' onClick={handleLogout}>Logout</div>
                  </li>
                    :
                  <li>
                    <Link className='link-li' to='/login'>
                      <div className='link-li' onClick={() => setVisible(true)}>Login</div>
                    </Link>
                  </li>
                  }
                </ul>
              </div>
            </div>
          </div>
        </nav>
      <Routes>
        <Route path='/:userId' element={<Home userId={userId} />} exact></Route>
        <Route path="/" element={<Home isAuthenticated={isAuthenticated} />} exact></Route>
        <Route path="/:userId/top" element={<Top/>} exact></Route>
        <Route path="/login" element={<Auth visible={visible} setVisible={setVisible} authenticated={authenticated}/>}></Route>
        <Route path="/:userId/watched" element={<Watched userId={userId}/>}></Route>
        <Route path="/:userId/watchlist" element={<Watchlist/>}></Route>
        <Route path='/movie/:title' element={<Movie/>}></Route>
      </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
