import React, {useState} from 'react'
import axios from 'axios'

function Register({goLogin}) {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
    })

    const handleChange = (e) => {
        const { name, value } = e.target
        setFormData({ ...formData, [name]: value})
    }

const handleRegister = async (e) => {
    e.preventDefault()
    try {
        const response = await axios.post('http://localhost:3000/users/register', formData, {
            headers: {
                'Content-Type': 'application/json',
            }
        })
        if (response.status === 200) {
            console.log(response)
        }
    } catch (err) {
        console.log(err)
    }
}

  return (
    <div className="register-card card text-center">
        <h2 className='create-account'>Create an Account</h2>
        <div className="card-body">
            <form className="register-form" action="/register" method='POST'>
                <div className="username form-item">
                    <label className='register-label' htmlFor="username">Username</label>
                    <input className='register-input' name='username' value={formData.username} onChange={handleChange} type="text" />
                </div>
                <div className="email form-item">
                    <label className='register-label' htmlFor="email">Email Address</label>
                    <input className='register-input' name='email' value={formData.email} onChange={handleChange} type="text" />
                </div>
                <div className="pass form-item">
                    <label className='register-label' htmlFor="password">Password</label>
                    <input className='register-input' name='password' value={formData.password} onChange={handleChange} type="password" />
                </div>
                <div className="confirm form-item">
                    <label className='register-label' htmlFor="confirm-password">Confirm Password</label>
                    <input className='register-input' name='confirmPassword' value={formData.confirmPassword} onChange={handleChange} type="password" />
                </div>
                <button onClick={(e) => handleRegister(e)} className="register-button">Register</button>
            </form>
        </div>
        <div className="card-footer-register">
            <div className="login-back">Have an account? <span className='like-link' onClick={goLogin}>Login</span></div>
        </div>
    </div>
  )
}

export default Register
