import React, {useState} from 'react'
import axios from 'axios'

function Register({goLogin}) {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
    })

    const handleChange = (e) => {
        const { name, value } = e.target
        setFormData({ ...formData, [name]: value})
        console.log(e.target)
    }
/*const handleRegister = async () => {
    try {
        const response = await axios.post('http://localhost:3000/users/login')
    } catch (err) {
        console.log(err)
    }
}*/
  return (
    <div className="card text-center">
        <h2 className='create-account'>Create an Account</h2>
        <div className="card-body">
            <form className="login-form" action="/register" method='POST'>
                <div className="username form-item">
                    <label className='login-label' htmlFor="username">Username</label>
                    <input className='login-input' name='username' value={formData.username} onChange={handleChange} type="text" />
                </div>
                <div className="email form-item">
                    <label className='login-label' htmlFor="email">Email Address</label>
                    <input className='login-input' name='email' type="text" />
                </div>
                <div className="pass form-item">
                    <label className='login-label' htmlFor="password">Password</label>
                    <input className='login-input' name='password' type="password" />
                </div>
                <div className="confirm form-item">
                    <label className='login-label' htmlFor="confirm-password">Confirm Password</label>
                    <input className='login-input' name='confirm' type="password" />
                </div>
                <button className="register-button">Register</button>
            </form>
        </div>
        <div className="card-footer">
            <div className="login-back">Have an account? <span className='like-link' onClick={goLogin}>Login</span></div>
        </div>
    </div>
  )
}

export default Register
