import React, { useEffect , useState} from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

export default function Watched() {
  const { userId } = useParams();
  const [loading, setLoading] = useState(true);
  const [watched, setWatched] = useState([])
  const [error, setError] = useState(null)
  useEffect(() => {
    axios.get(`http://localhost:3000/movies/${userId}/watched`)
    .then(response => {
      const data = response.data
      setWatched(data)
      setLoading(false)
    })
    .catch((error) => {
      console.error('Error fetching user data:', error);
      setError(error)
      setLoading(false)
  })}, [userId])

  return (
    <div>
      <h1 className='watched-title'>Watched</h1>
      {watched.map((movie, index) => 
        <div className='watched-movies' key={index}>
          <img width="700px" className='single-poster' src={movie.poster}/>
          <h3 className="single-title">{movie.title}</h3>
          <div className="single-small">
            <div className="single-left">
              <p className="single-year">{movie.year}</p>
              <p className="single-runtime">{movie.runtime}</p>
            </div>
            <p className="single-runtime">{movie.type}</p>
          </div>
        </div>
      )}
    </div>
  )
}
