import React from 'react'
import { useState } from 'react'

export default function Home() {
    const [showName, setShowName] = useState("")
    const [input, setInput] = useState("")
    const [title, setTitle] = useState(null)
    const [plot, setPlot] = useState(null)
    const [poster, setPosters] = useState(null)

    const handleInput = (e) => {
        showName = e.target.value
        setInput(showName)
    }

    fetch(`http://www.omdbapi.com/?t=${showName}&apikey=3244219b`)
        .then(response => response.json())
        .then(data => { console.log(data)
            setTitle(data.Title)
            setPlot(data.Plot)
            setPosters(data.Poster)
        })
  return (
    <div>
        <div className="movie">
            <input type="text" onChange={handleInput} name="findMovie" value={input} placeholder='Find a show' id="" />
            <div>
                <h3>{title}</h3>
                <img src={poster} alt="" />
                <p>{plot}</p>
            </div>
        </div>
    </div>
  )
}
