import React from 'react'
import { useState } from 'react'

export default function Home() {
    const [showName, setShowName] = useState([])
    const [input, setInput] = useState()
    const [title, setTitle] = useState(null)
    const [plot, setPlot] = useState(null)
    const [poster, setPoster] = useState(null)
    const [released, setReleased] = useState(null)
    const [runtime, setRuntime] = useState(null)
    const [director, setDirector] = useState(null)
    const [genre, setGenre] = useState(null)
    const [writer, setWriter] = useState(null)
    const [actors, setActors] = useState(null)
    const [country, setCountry] = useState(null)
    const [imdbRating, setImdbRating] = useState(null)
    const [type, setType] =useState(null)
    const [year, setYear] = useState(null)

    const handleInput = (e) => {
        const movieName = e.target.value;
        setInput(movieName);
        fetchMovies(movieName);
    }

    const fetchMovies = (movieName) => {
        fetch(`http://www.omdbapi.com/?s=${movieName}&apikey=3244219b`)
            .then(response => response.json())
            .then(data => {
                if (data.Search) {
                    const lastFiveMovies = data.Search.slice(-5);
                    setShowName(lastFiveMovies);
                }
            });
    };

    /*fetch(`http://www.omdbapi.com/?t=${showName}&apikey=3244219b`)
        .then(response => response.json())
        .then(data => { console.log(data)
            setTitle(data.Title)
            setPlot(data.Plot)
            setPoster(data.Poster)
            setReleased(data.Released)
            setRuntime(data.Runtime)
            setDirector(data.Director)
            setGenre(data.Genre)
            setWriter(data.Writer)
            setActors(data.Actors)
            setCountry(data.Country)
            setImdbRating(data.imdbRating)
            setType(data.Type)
            setYear(data.Year)
            console.log("SHOW NAME: ", showName)
        })*/
  return (
    <div>
        <div className="movie">
            <input className='searchField' type="text" onChange={handleInput} name="findMovie" value={input} placeholder='Find a show' id="" />
            {input?
                <div className='scroll'>
                <div className="text">
                    <h4>{showName.Title}</h4>
                    <div className="small">
                        <p>{year}</p>
                        <p>o</p>
                        <p>{showName.Runtime?showName.Runtime.split(' '):"null"}</p>
                        <p>o</p>
                        <p>{type? type[0].toUpperCase() + type.slice(1, type.length) : null}</p>
                    </div>
                </div>
                <div className="image">
                    <img src={showName.Poster} width="60px" height="80px" alt="" />
                </div>
            </div> : null
            }
            
            {/*<div>
                {input?
                    <>
                    <h3>{title}</h3>
                    <p>{type}</p>
                    <p>{released}</p>
                    <p>{runtime}</p>
                    <p>IMDB RATING: {imdbRating}</p>
                    <p>Your Rating</p>
                    <img src={poster} alt="" />
                    <p>{genre}</p>
                    <p>Director: {director}</p>
                    <p>Writes: {writer}</p>
                    <p>Actors: {actors}</p>
                    <p>{plot}</p>
                    <p>Country: {country}</p>
                    </> 
                    : null
                }
            </div>*/}
        </div>
    </div>
  )
}
