import React, { useEffect , useState} from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

export default function Watched() {
  const { userId } = useParams();
  const [loading, setLoading] = useState(true);
  const [watched, setWatched] = useState([])
  const [error, setError] = useState(null)

  useEffect(() => {
    axios.get(`http://localhost:3000/movies/${userId}/watched`)
    .then(response => {
      const data = response.data
      setWatched(data)
      setLoading(false)
    })
    .catch((error) => {
      console.error('Error fetching user data:', error);
      setError(error)
      setLoading(false)
  })}, [userId])

  const deleteMovie = (index) => {
    const movieToDelete = watched[index]
    console.log(movieToDelete.id)
    axios.delete(`http://localhost:3000/movies/${userId}/watched/${movieToDelete.id}`)
    .then((response) => {
      console.log(response)
      const updatedWatched = watched.filter((movie, i) => i !== index);
      setWatched(updatedWatched);
    })
    .catch((error) => {
      console.log(error)
    })
  }

  return (
    <div className='watched-page'>
      <h1 className='watched-title'>Watched</h1>
      <div className="watched-container">
        {watched.map((movie, index) => 
          <div className='watched-movies' key={index}>
            <p onClick={() => deleteMovie(index)} className="watched-cross">x</p>
            <img width="180px" className='single-poster' src={movie.poster} alt='poster'/>
            <h3 className="single-title">{movie.title}</h3>
            <div className="single-small">
              <div className="single-left">
                <p className="single-year">{movie.year}</p>
                <p className="single-runtime">- {movie.runtime}</p>
              </div>
              <p className="single-type">{movie.type[0].toUpperCase() + movie.type.slice(1, movie.type.length)}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}
