import React, { useState, useEffect } from 'react';

export default function Home() {
    const [input, setInput] = useState("");
    const [movies, setMovies] = useState([]);

    const debounce = (func, delay) => {
        let timeoutId;
        return function (...args) {
            clearTimeout(timeoutId);
            timeoutId = setTimeout(() => func(...args), delay);
        };
    };

    const fetchMovies = (query) => {
        fetch(`http://www.omdbapi.com/?s=${query}&apikey=3244219b`)
            .then(response => response.json())
            .then(data => {
                if (data.Search) {
                    const lastFiveMovies = data.Search.slice(-5);
                    setMovies(lastFiveMovies);
                }
            });
    };

    const handleInput = (e) => {
        const query = e.target.value;
        setInput(query);
        fetchMovies(query);
    }

    return (
        <div>
            <div className="movie">
                <input className='searchField' type="text" onChange={handleInput} name="findMovie" value={input} placeholder='Find a show' />
                {movies.length > 0 && input ?
                    <div className='scroll'>
                        {movies.map((movie, index) => (
                            <div key={index} className="text">
                                <h4>{movie.Title}</h4>
                                <div className="small">
                                    <p>{movie.Year}</p>
                                    <p>o</p>
                                    <p>{movie.Type}</p>
                                </div>
                            </div>
                        ))}
                    </div> : null
                }
            </div>
        </div>
    );
}
