import React, { useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

export default function Login({registerButton, passwordButton}) {
    const [loginData, setLoginData] = useState({
        username: '',
        password: ''
    })

    const [authenticated, setAuthenticated] = useState(false)
    const nav = useNavigate()

    const handleChange = (e) => {
        const {name, value} = e.target
        setLoginData({...loginData, [name]: value})
    }

    const handleLogin = (e) => {
        try {
            axios.post('http://localhost:3000/users/auth', loginData, {
                headers: {
                    'Content-Type': 'application/json'
                }
            }
        )
        .then((response) => {
            console.log("RESPONSE: ", response.data.userId);
            if (response.status === 200) {
                const userId = response.data.userId
                localStorage.setItem("isAuthenticated", true)
                console.log("authenticated: ", authenticated);
                nav(`/${userId}`)
            } else {
                console.log("REFUSED")
            }
        })
        } catch (err) {
            console.log(err)
        }
    }

    return (
        <div className="card text-center">
            <h2 className='welcome-back'>Welcome Back</h2>
            <div className="movie-icon">
                <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" fill="currentColor" class="bi bi-film" viewBox="0 0 16 16">
                    <path d="M0 1a1 1 0 0 1 1-1h14a1 1 0 0 1 1 1v14a1 1 0 0 1-1 1H1a1 1 0 0 1-1-1V1zm4 0v6h8V1H4zm8 8H4v6h8V9zM1 1v2h2V1H1zm2 3H1v2h2V4zM1 7v2h2V7H1zm2 3H1v2h2v-2zm-2 3v2h2v-2H1zM15 1h-2v2h2V1zm-2 3v2h2V4h-2zm2 3h-2v2h2V7zm-2 3v2h2v-2h-2zm2 3h-2v2h2v-2z"/>
                </svg>
            </div>
            <div className="card-body">
                <form className="login-form" action="/auth" method='POST'>
                    <div className="email form-item">
                        <label className='login-label' htmlFor="username">Username</label>
                        <input className='login-input' name='username' value={loginData.username} onChange={(e) => handleChange(e)} type="text" />
                    </div>
                    <div className="pass form-item">
                        <label className='login-label' htmlFor="password">Password</label>
                        <input className='login-input' name='password' value={loginData.password} onChange={(e) => handleChange(e)} type="password" />
                    </div>
                    <div className="middle">
                        <div className="remember">
                            <input type="checkbox" name="remember" id="" />
                            <p className='remember-text'>Remember Me</p>
                        </div>
                        <div className="forgot-pass">
                            <span onClick={passwordButton} className='like-link'>Forgot Password</span>
                        </div>
                    </div>
                    <button onClick={(e) => handleLogin(e)} className="login-button">Login</button>
                </form>
            </div>
            <div className="card-footer">
                <div className="sign">Don't have an account? <span className='like-link' onClick={registerButton}>Register</span></div>
            </div>
        </div>
    )
}
