import React from 'react'
import { useState } from 'react'

export default function Home() {
    const [shows, setShows] = useState([])
    const [input, setInput] = useState()
    const [display, setDisplay] = useState(false)
    const [movieName, setMovieName] = useState("")
    const [release, setRelease] = useState(null)
    const [movieData, setMovieData] = useState({})

    const fetchMovies = (query) => {
        fetch(`http://www.omdbapi.com/?s=${query}&apikey=3244219b`)
            .then(response => response.json())
            .then(data => {
                if (data.Search) {
                    const lastFiveMovies = data.Search.slice(-5);
                    setShows(lastFiveMovies);
                }
            });
    };

    const fetchMovieDetails = () => {
        setDisplay(true);
        console.log(movieName)
        fetch(`http://www.omdbapi.com/?s=${movieName}&apikey=3244219b`)
            .then(response => response.json())
            .then(data => {
                setMovieData(data)
                console.log(data.Search)
            })
    }

    const handleInput = (e) => {
        const query = e.target.value;
        setInput(query);
        fetchMovies(query);
        setDisplay(false)
    }


  return (
    <div>
        <div className="movie">
            <input className='searchField' type="text" onChange={handleInput} name="findMovie" value={input} placeholder='Find a show' id="" />
            {input && shows.length > 0 && display === false?
                <div>
                    {shows.map((show, index) => (
                        <div key={index} className='scroll' onClick={() => {/*console.log(show.title)*/; setMovieName(show.Title); fetchMovieDetails();}}>
                            <div className="text">
                                <h4>{show.Title}</h4>
                                <div className="small">
                                    <p>{show.Year}</p>
                                    <p>o</p>
                                    <p>{show.Runtime}</p>
                                    <p>o</p>
                                    <p>{show.Type? show.Type[0].toUpperCase() + show.Type.slice(1, show.Type.length) : null}</p>
                                    <p style={{display: "none"}}>{show.Released}{show.imdbRating}</p>
                                </div>
                            </div>
                            <div className="image">
                                <img src={show.Poster ? show.Poster : null} width="60px" height="80px" alt="" />
                            </div>
                        </div>
                        ))
                    }
                </div> 
                : null }
                {/*<div className='movie-container'>
                    <h3>{movie.Title}</h3>
                    <p>{movie.Type? movie.Type[0].toUpperCase() + movie.Type.slice(1, movie.Type.length) : null}</p>
                    <p>{movie.Released}</p>
                    <p>{movie.Runtime}</p>
                    <p>IMDB RATING: {movie.imdbRating}</p>
                    <p>Your Rating</p>
                    <img src={movie.Poster} alt="" />
                    <p>{movie.Genre}</p>
                    <p>Director: {movie.Director}</p>
                    <p>Writes: {movie.Writer}</p>
                    <p>Actors: {movie.Actors}</p>
                    <p>{movie.Plot}</p>
                    <p>Country: {movie.Country}</p>
                </div>*/}
            
        </div>
    </div>
  )
}
